// Import React and useState at the top of your file
import React, { useState } from "react";

function App() {
  const [bots, setBots] = useState([]); // Fetch and store bot data
  const [army, setArmy] = useState([]); // Store enlisted bots

  // Function to enlist a bot
  const enlistBot = (bot) => {
    // Check if the bot is not already enlisted
    if (!army.find((enlistedBot) => enlistedBot.id === bot.id)) {
      setArmy([...army, bot]);
    }
  };

  // Function to release a bot
  const releaseBot = (bot) => {
    const updatedArmy = army.filter((enlistedBot) => enlistedBot.id !== bot.id);
    setArmy(updatedArmy);
  };

  // Implement functions to fetch data

  return (
    <div className="App">
      <h1>Bot Battlr</h1>
      <BotCollection bots={bots} enlistBot={enlistBot} releaseBot={releaseBot} />
      <YourBotArmy army={army} releaseBot={releaseBot} />
    </div>
  );
}
